import React, { useState } from 'react';
import { Text, View, ScrollView, KeyboardAvoidingView, Platform } from 'react-native';
import { RectButton } from 'react-native-gesture-handler';
import  { Feather } from '@expo/vector-icons';

import { Background } from '../../components/Background';
import { CategorySelect } from '../../components/CategorySelect';
import { Header } from '../../components/Header';
import { GuildIcon } from '../../components/GuildIcon/inde';
import { SmallInput } from '../../components/SmallInput';
import { TextArea } from '../../components/TextArea';

import { styles } from './styles';
import { theme } from '../../global/styles/theme';
import { Button } from '../../components/Button';
import { ModalView } from '../../components/ModalView';
import { Guilds } from '../Guilds';
import { GuildProps } from '../../components/Guild';


export function AppointmentCreate(){
  const [category, setCategory] = useState('')
  const [opemGuildModal, setOpenGuildModal] = useState(false);
  const [guild, setGuild ]= useState<GuildProps>({} as GuildProps);

  function handleOpenGuilds(){
    setOpenGuildModal(true);
  }
  function handleCloseGuilds(){
    setOpenGuildModal(false);
  }
  function handleGuildsSelect(guildSelect : GuildProps){
    setGuild(guildSelect);
    setOpenGuildModal(false);
  }
  function handleCategorySelect(categoryId: string)
  {
    setCategory(categoryId);
  }
    return(
      <KeyboardAvoidingView 
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.container}
      >
        <Background>
          <ScrollView>
            <Header 
            title="Agendar partida"
            />
            <Text style={[styles.label,{marginLeft:24, marginTop:36, marginBottom:18}]}>
              Categoria
            </Text>
            <CategorySelect 
              hasCheckBox
              setCategory={handleCategorySelect}
              categorySelected={category}        
            />
            <View style={styles.form}>
              <RectButton onPress={handleOpenGuilds}>
                <View style={styles.select}>
                  {
                    guild.icon ? <View style={styles.image} /> :
                    <GuildIcon />
                  }
                  <View style={styles.selectBody}>
                    <Text style={styles.label}>
                      {guild.name ? guild.name : 'Selecione um servidor'}
                    </Text>
                  </View>
                  <Feather
                  name='chevron-right'
                  color={theme.colors.heading}
                  size={18}
                  />
                </View>
              </RectButton>
              <View style={styles.field}>
                <View>
                  <Text style={[styles.label, {marginBottom: 12}]}>
                    Dia e mês
                  </Text>
                    <View style={styles.column}>
                      <SmallInput maxLength={2}/>
                        <Text style={styles.divider}>
                          /
                        </Text>
                      <SmallInput maxLength={2}/>
                    </View>
                </View>
                <View>
                  <Text style={[styles.label, {marginBottom: 12}]}>
                    Hora e minuto
                  </Text>
                    <View style={styles.column}>
                      <SmallInput maxLength={2}/>
                        <Text style={styles.divider}>
                          :
                        </Text>
                      <SmallInput maxLength={2}/>
                    </View>
                </View>
              </View>
              <View style={[styles.field, {marginBottom: 12}]}>
                <Text style={styles.label}>
                  Descrição
                </Text>
                <Text style={styles.caractersLimit}>
                  Max 100 caracteres
                </Text>
              </View>
              <TextArea
                multiline
                maxLength={100}
                numberOfLines={5}
                autoCorrect={false}
              />
              <View style={styles.footer}>
                <Button title='Agendar'/>
              </View>
            </View>
          </ScrollView>
        </Background>
        <ModalView visible={opemGuildModal} closeModal={handleCloseGuilds}>
          <Guilds handleGuildSelect={handleGuildsSelect} />
        </ModalView>
      </KeyboardAvoidingView>
    );
}
